import React, { useState } from 'react';
import { Route, Switch } from 'react-router-dom';
import { Grid } from '@material-ui/core';
import Login from './Login';
import Register from './Register';
import { registerUser } from '../../Utils/api';

const Landing = (props) => {
	const [registerModalOpen, setRegisterModalOpen] = useState(true);
	const [registerFirstName, setRegisterFirstName] = useState('');
	const [registerLastName, setRegisterLastName] = useState('');
	const [registerEmail, setRegisterEmail] = useState('');

	const toggleRegisterModal = () => {
		console.log(registerModalOpen);
		setRegisterModalOpen(!registerModalOpen);
	};

	const registerUser = async () => {
		try {
			let user = await registerUser();
		} catch (error) {
			console.log(error);
		}
	};

	const handleRegisterFormChange = (e) => {
		console.log(e.target.name + ' : ' + e.target.value);
		/*this.setState({
			[e.target.name]: e.target.value,
			//createFormErrors: {},
		});*/
	};

	return (
		<Grid container direction="column" alignItems="center">
			<Switch>
				<Route path={['/register']}>
					<Register
						registerUser={registerUser}
						handleFormChange={handleRegisterFormChange}
						registerModalOpen={registerModalOpen}
						firstName={registerFirstName}
						lastName={registerLastName}
						email={registerEmail}
						handleRegisterModalClose={toggleRegisterModal}
					/>
				</Route>
			</Switch>
			<Switch>
				<Route path={['/', '/login']}>
					<Login
						registerModalOpen={registerModalOpen}
						handleRegisterModalOpen={toggleRegisterModal}
					/>
				</Route>
			</Switch>
		</Grid>
	);
};

export default Landing;
