import React from 'react';
import { Link } from 'react-router-dom';
import {
	Grid,
	makeStyles,
	TextField,
	Button,
	Typography,
} from '@material-ui/core';
import StyledOutlineButton from '../Components/StyledOutlineButton';
import logo from '../Assets/logo_lightBlue.svg';

import colors from '../Styles/colors';

const useStyles = makeStyles((theme) => ({
	root: {
		height: '100vh',
		width: '100%',
		backgroundColor: '#bbbbbb',
		overflow: 'auto',
		display: 'flex',
	},
	leftContainer: { height: '100vh', width: '100%', backgroundColor: '#bbbbbb' },
	rightContainer: {
		height: '100vh',
		width: '100%',
		backgroundColor: `${colors.darkBlue}`,
	},
	loginRow: { paddingRight: theme.spacing(2) },
	inviteBanner: {
		fontSize: '2rem',
	},
	logo: {
		height: '40px',
		width: '40px',
	},
}));

const Landing = (props) => {
	const classes = useStyles();

	return (
		<Grid className={classes.root} container direction="row">
			<Grid item xs={6}>
				<Grid className={classes.leftContainer} container direction="column">
					Left side
				</Grid>
			</Grid>
			<Grid item xs={6}>
				<Grid className={classes.rightContainer} container direction="column">
					<Grid
						className={classes.loginRow}
						justify="center"
						container
						direction="row"
					>
						<Grid item>
							<TextField label="username"></TextField>
						</Grid>
						<Grid item>
							<TextField type="password" label="password"></TextField>
						</Grid>
						<Grid item>
							<StyledOutlineButton>Log In</StyledOutlineButton>
						</Grid>
					</Grid>
					<Grid item>
						<img src={logo} className={classes.logo} alt="logo" />
					</Grid>
					<Grid
						className={classes.loginRow}
						justify="center"
						container
						direction="row"
					>
						<Grid item>
							<Typography className={classes.inviteBanner}>
								See what's happening in Airen's world right now.
							</Typography>
						</Grid>
					</Grid>
					<Grid item>Join Twitter Today</Grid>
					<Grid item>
						<StyledOutlineButton>Sign up</StyledOutlineButton>
					</Grid>
					<Grid item>
						<StyledOutlineButton>Log In</StyledOutlineButton>
					</Grid>
				</Grid>
			</Grid>
		</Grid>
	);
};

export default Landing;
