import React from 'react';
import { Link } from 'react-router-dom';
import { Grid, makeStyles, TextField, Button } from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
	root: {
		height: '100vh',
		width: '100%',
		backgroundColor: '#bbbbbb',
		overflow: 'auto',
	},
	leftContainer: { height: '100vh', width: '100%', backgroundColor: '#ffffff' },
	rightContainer: {
		height: '100vh',
		width: '100%',
		backgroundColor: '#000000',
	},
	loginRow: {},
}));

const Landing = (props) => {
	const classes = useStyles();

	return (
		<Grid className={classes.root} container direction="row">
			<Grid item xs={6}>
				<Grid className={classes.leftContainer} container direction="column">
					Left side
				</Grid>
			</Grid>
			<Grid item xs={6}>
				<Grid className={classes.root} container direction="column">
					<Grid className={classes.loginRow} container direction="row">
						<TextField label="username"></TextField>
						<TextField type="password" label="password"></TextField>
						<Button>Log In</Button>
					</Grid>
				</Grid>
			</Grid>
		</Grid>
	);
};

export default Landing;
