import React from 'react';
import { Link } from 'react-router-dom';
import {
	Grid,
	makeStyles,
	TextField,
	Button,
	Typography,
} from '@material-ui/core';
import CssBaseline from '@material-ui/core/CssBaseline';
import StyledOutlineButton from '../Components/StyledOutlineButton';
import StyledFillButton from '../Components/StyledFillButton';
import logo from '../Assets/logo_lightBlue.svg';

import colors from '../Styles/colors';

const useStyles = makeStyles((theme) => ({
	root: {
		height: '100vh',
		width: '100%',
		overflow: 'auto',
		backgroundColor: '#000000',
		display: 'flex',
		spacing: '0',
	},
	leftContainer: { height: '100vh', width: '100%', backgroundColor: '#bbbbbb' },
	rightContainer: {
		height: '100vh',
		width: '100%',
		backgroundColor: `${colors.darkBlue}`,
		alignItems: 'center',
	},
	loginRow: { marginTop: theme.spacing(4) },
	loginTextField: {
		margin: theme.spacing(1),
	},
	loginButton: {
		width: '90px',
		fontSize: '.75rem',
	},
	inviteBanner: {
		fontSize: '1.5rem',
		color: `${colors.lightBlue}`,
	},
	logo: {
		height: '20px',
		width: '20px',
	},
}));

const Landing = (props) => {
	const classes = useStyles();

	return (
		<CssBaseline>
			<Grid className={classes.root} container direction="row">
				<Grid item xs={6}>
					<Grid className={classes.leftContainer} container direction="column">
						Left side
					</Grid>
				</Grid>
				<Grid item xs={6}>
					<Grid className={classes.rightContainer} container direction="column">
						<Grid
							className={classes.loginRow}
							justify="center"
							container
							direction="row"
						>
							<Grid item>
								<TextField
									className={classes.loginTextField}
									label="username"
									variant="filled"
								></TextField>
							</Grid>
							<Grid item>
								<TextField
									className={classes.loginTextField}
									type="password"
									label="password"
									variant="filled"
								></TextField>
							</Grid>
							<Grid item>
								<StyledOutlineButton className={classes.loginButton}>
									Log In
								</StyledOutlineButton>
							</Grid>
						</Grid>
						<Grid item>
							<img src={logo} className={classes.logo} alt="logo" />
						</Grid>
						<Grid
							className={classes.loginRow}
							justify="center"
							container
							direction="row"
						>
							<Grid item>
								<Typography className={classes.inviteBanner}>
									See what's happening in Airen's world right now.
								</Typography>
							</Grid>
						</Grid>
						<Grid item>
							<Typography>Join Twitter Today</Typography>
						</Grid>
						<Grid item>
							<StyledFillButton>Sign up</StyledFillButton>
						</Grid>
						<Grid item>
							<StyledOutlineButton>Log In</StyledOutlineButton>
						</Grid>
					</Grid>
				</Grid>
			</Grid>
		</CssBaseline>
	);
};

export default Landing;
