import React from 'react';
import { useHistory } from 'react-router-dom';
import {
	Grid,
	DialogTitle,
	Typography,
	TextField,
	Dialog,
	DialogContent,
	DialogContentText,
	Divider,
	makeStyles,
} from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
	root: {
		flexGrow: 1,
	},
	dialog: {
		marginTop: '2rem',
		display: 'flex',
		flexDirection: 'column',
		alignItems: 'center',
	},
	dialog: {
		marginTop: '2rem',
		display: 'flex',
		flexDirection: 'column',
		alignItems: 'center',
	},
	dialogTitle: {
		fontSize: '2rem',
	},
	form: {
		width: '100%',
	},
	inputContainer: {
		alignContent: 'center',
		alignItems: 'center',
		justifyContent: 'center',
		padding: '2rem',
		width: '100%',
	},
	statusLabel: {
		//color: `${colors.darkGray}`,
	},
}));

const Register = (props) => {
	const classes = useStyles();
	const history = useHistory();

	const handleClose = () => {
		props.handleRegisterModalClose();
		history.push('/');
	};

	const handleSubmit = () => {
		props.registerUser();
	};

	const handleChange = (e) => {
		props.handleFormChange(e);
	};

	/*const successDialogClose = () => {
		setCreateSuccess(null);
	};*/

	return (
		<Dialog
			aria-labelledby="simple-dialog-title"
			open={true}
			onClose={handleClose}
			fullWidth={true}
			maxWidth={'sm'}
		>
			<div className={classes.dialog}>
				<DialogTitle id="simple-dialog-title">
					<Typography>Create a Prospect</Typography>
				</DialogTitle>
			</div>
			<form method="POST" onSubmit={handleSubmit} className={classes.form}>
				<Grid
					container
					alignItems="center"
					direction="column"
					justify="center"
					alignItems="center"
					spacing={2}
					className={classes.inputContainer}
				>
					<Grid item sm={8}>
						<TextField
							label="First name"
							name="createFormFirstName"
							variant="outlined"
							fullWidth
							hintText="First Name"
							onChange={handleChange}
							value={firstName}
							error={'firstName' in errors}
							helperText={errors.firstName}
						/>
					</Grid>
					<Grid item sm={8}>
						<TextField
							label="Last name"
							name="createFormLastName"
							variant="outlined"
							fullWidth
							hintText="Last Name"
							onChange={handleChange}
							value={lastName}
							error={'lastName' in errors}
							helperText={errors.lastName}
						/>
					</Grid>
					<Grid item sm={8}>
						<TextField
							label="Email"
							name="createFormEmail"
							variant="outlined"
							hintText="Email"
							fullWidth
							onChange={handleChange}
							value={email}
							error={'email' in errors}
							helperText={errors.email}
						/>
					</Grid>
					<Grid item sm={8}>
						<StyledButton onClick={handleSubmit}>Submit</StyledButton>
					</Grid>
				</Grid>
			</form>
		</Dialog>
	);
};

export default Register;
