import React from 'react';
import { useHistory } from 'react-router-dom';
import {
	Grid,
	DialogTitle,
	Typography,
	TextField,
	Dialog,
	DialogContent,
	DialogContentText,
	Divider,
	makeStyles,
} from '@material-ui/core';
import StyledFillButton from '../../Components/StyledFillButton';

const useStyles = makeStyles((theme) => ({
	root: {
		flexGrow: 1,
	},
	dialog: {
		marginTop: '2rem',
		display: 'flex',
		flexDirection: 'column',
		alignItems: 'center',
	},
	dialog: {
		marginTop: '2rem',
		display: 'flex',
		flexDirection: 'column',
		alignItems: 'center',
	},
	registerTitle: {
		fontSize: '2rem',
		color: ``,
	},
	form: {
		width: '100%',
	},
	inputContainer: {
		alignContent: 'center',
		alignItems: 'center',
		justifyContent: 'center',
		padding: '2rem',
		width: '100%',
	},
	registerInputFieldRow: { marginBottom: theme.spacing(1) },
	statusLabel: {
		//color: `${colors.darkGray}`,
	},
}));

const Register = (props) => {
	const classes = useStyles();
	const history = useHistory();

	const {
		firstName,
		lastName,
		email,
		handleRegisterModalClose,
		registerUser,
		setRegisterFirstName,
		setRegisterLastName,
		setRegisterEmail,
		setRegisterPassword,
		setRegisterConfirmPassword,
	} = props;

	const handleClose = () => {
		handleRegisterModalClose();
		history.push('/');
	};

	const handleSubmit = () => {
		registerUser();
	};

	/*const successDialogClose = () => {
		setCreateSuccess(null);
	};*/

	return (
		<Dialog
			aria-labelledby="simple-dialog-title"
			open={true}
			onClose={handleClose}
			fullWidth={true}
			maxWidth={'sm'}
		>
			<div className={classes.dialog}>
				<DialogTitle id="simple-dialog-title">
					<Typography className={classes.registerTitle}>
						Create Your Account
					</Typography>
				</DialogTitle>
			</div>
			<form method="POST" onSubmit={handleSubmit} className={classes.form}>
				<Grid
					container
					alignItems="center"
					direction="column"
					justify="center"
					alignItems="center"
					spacing={2}
					className={classes.inputContainer}
				>
					<Grid
						container
						alignItems="center"
						direction="row"
						justify="center"
						alignItems="center"
						spacing={2}
						className={classes.registerInputFieldRow}
					>
						<Grid item sm={4}>
							<TextField
								label="First name"
								name="registerFormFirstName"
								variant="outlined"
								fullWidth
								onChange={(e) => setRegisterFirstName(e.target.value)}
								value={firstName}
								//error={'firstName' in errors}
								//helperText={errors.firstName}
							/>
						</Grid>
						<Grid item sm={4}>
							<TextField
								label="Last name"
								name="registerFormLastName"
								variant="outlined"
								fullWidth
								onChange={(e) => setRegisterLastName(e.target.value)}
								value={lastName}
								//error={'lastName' in errors}
								//helperText={errors.lastName}
							/>
						</Grid>
					</Grid>

					<Grid
						container
						alignItems="center"
						direction="row"
						justify="center"
						alignItems="center"
						spacing={2}
						className={classes.registerInputFieldRow}
					>
						<Grid item sm={8}>
							<TextField
								label="Email"
								name="registerFormEmail"
								variant="outlined"
								fullWidth
								onChange={(e) => setRegisterEmail(e.target.value)}
								value={email}
								//error={'email' in errors}
								//helperText={errors.email}
							/>
						</Grid>
					</Grid>

					<Grid
						container
						alignItems="center"
						direction="row"
						justify="center"
						alignItems="center"
						className={classes.registerInputFieldRow}
						spacing={1}
					>
						<Grid item sm={4}>
							<TextField
								label="Password"
								name="registerFormPassword"
								variant="outlined"
								fullWidth
								onChange={(e) => setRegisterPassword(e.target.value)}
								value={email}
								//error={'email' in errors}
								//helperText={errors.email}
							/>
						</Grid>
						<Grid item sm={4}>
							<TextField
								label="Confirm Password"
								name="registerFormConfirmPassword"
								variant="outlined"
								fullWidth
								onChange={(e) => setRegisterConfirmPassword(e.target.value)}
								value={email}
								//error={'email' in errors}
								//helperText={errors.email}
							/>
						</Grid>
					</Grid>

					<Grid item sm={8}>
						<StyledFillButton onClick={handleSubmit}>Submit</StyledFillButton>
					</Grid>
				</Grid>
			</form>
		</Dialog>
	);
};

export default Register;
